//s - coordinates origin
#define MAX_STEPS 100
#define MAX_DIST 100.  
#define SURF_DIST .01
//dot because of float and integer
float GetDist(vec3 p) {
 vec4 s =vec4(0,1,6,1);
 float sdA = length(p-vec3(0,1,5))-1.;//spere distance
 float sdB = length(p-vec3(1,1,5))-1.;
 float sd = max(sdA,-sdB);
 float pd = p.y; //pd -planedist
 float d = min(pd,sd);
 return d;
 }
float RayMarch(vec3 ro,vec3 rd){ //raymarchfuntion
  float dO =0.;
  for(int i=0;i<MAX_STEPS;i++){
  vec3 p = ro +rd*dO;
  float ds = GetDist(p);
  dO+=ds;
  if(dO>MAX_DIST || ds<SURF_DIST) break;
  }
  return dO;
}
vec3 GetNormal(vec3 p){  //light
 float d = GetDist(p);
 vec2 e = vec2 (.01,0.);
 vec3 n = d - vec3(
 GetDist(p-e.xyy),
 GetDist(p-e.yxy),
 GetDist (p-e.yyx));
 return normalize (n);
}
 float GetLight (vec3 p){  //light
 vec3 lightPos = vec3 (2.5,5,6);
 vec3 l =normalize(lightPos-p);
 vec3 n = GetNormal(p);
  float dif = clamp(dot(n,l), 0.,1.);  // n-normal, l-light //clamp - hold the values
  float d = RayMarch(p+n*SURF_DIST*2.,l);   //shadow
  if(d<length(lightPos-p)) dif*=.1;   //shadow
 return dif;
 }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y; //middle

    // Time varying pixel color
    vec3 col =vec3(0);
    vec3 ro = vec3 (2.1,1,0);
    vec3 rd =normalize(vec3(uv.x,uv.y,1)); //check by deleting the normalize function (NORMALIZE MEANS THAT A LENGTH IS SETTING TO 1); 
    float d = RayMarch(ro,rd);
    vec3 p =ro + rd*d;
    float dif = GetLight(p);
    d/=9.;
    col = vec3(dif);


    // Output to screen
    fragColor = vec4(col, 1.0);
}
