float smoothstep_1(float x, float y, float z) {
    if(x > z) {
        return 0.0;
    } 
    if(x >= y) {
        return 1.0;
    }
    float a = (x - z) / (y - x);
    return a * a * (3.0 - 2.0 * a);
}

float radius(float itime) {
    float time = mod(itime, 5.0) * 1000.0;
    float d =0.;
    if(time < 2500.) {
        d = 2. - (2.1/2500.) * time;
    } else {
        d = -0.1 + (2.1/2500.) * (time - 2500.);
    }
    return d;
}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv =fragCoord/iResolution.xy; //坐标除以画布大小得到归一化uv坐标
    vec4 col1 = textureLod(iChannel1, uv, 0.);
    vec4 col2 = textureLod(iChannel0, uv, 0.);
    vec4 col = col1;
   // uv 居中
    uv=(uv*2.)-1.0;
    uv.x*=iResolution.x/iResolution.y; // 修正uv坐标未自动适应画布比例的问题
    //float r = radius(iTime);
    float time = mod(iTime, 5.0) * 1000.0;
    float r =0.;
    if(time < 2500.) {
        r = 2. - (2.1/2500.) * time;
        col = col1;
    } else {
        r = -0.1 + (2.1/2500.) * (time - 2500.);
        col = col2;
    }
    float d=length(uv) - r; // 取一段距离
    float c=0.; //存放值的变量
    c=smoothstep_1(0.,0.01,d ); //利用step函数得到
    vec4 x = vec4(c,c,c,1.0);
    if(x.r >= 255.0 && x.g >= 255.0 && x.b >= 255.0){
        fragColor = vec4(0.,0.,0.,1.0);
    } else {
        fragColor = col;
    }
}
