void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.7 * (fragCoord.xy - iResolution.xy / 2.0) / min(iResolution.x, iResolution.y);
    vec2 uvIter = uv;
    vec2 tempVec;

    float iterations = 50.0;

    vec3 col = vec3(0.0);

    for (float i = 0.0; i < iterations; i++) {
        tempVec = uvIter;
        uvIter.x = tempVec.x * tempVec.x - tempVec.y * tempVec.y + uv.x;
        uvIter.y = 2.0 * tempVec.x * tempVec.y + uv.y;
        if (length(uvIter) > 2.0) {
            col = vec3(1.0 - i / iterations);
            break;
        }
    }


    fragColor = vec4(col, 1.0);
}
