float dSomething(vec2 p, float r) {
    float blur = 1.5 / iResolution.y;
    return smoothstep(r + blur, r - blur, abs(p.x - p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.y;

    vec2 p = cos(uv * 100.0 + iTime);

    float dist = dSomething(p, (1.0 + sin(iTime)) / 2.0);

    fragColor = vec4(vec3(dist), 1.0);
}
