const float pi = 3.1415926535;

float chladni(float x, float y, float a, float b, float m, float n)
{
    return a * sin(pi*n*x) * sin(pi*m*y) + b * sin(pi*m*x) * sin(pi*n*y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    float uvl = abs(uv.y);
    float l = length(uv);   
    float amp = sin(iTime * 0.05) * 1.;
    uv.x += cos(l * 0.024) * .2 * amp;
    uv.y += sin(l * 0.025) * .3 * amp;
    float ts = 0.75 + sin(iTime * 0.075) * 0.5;
    float tc = 0.65 + cos(iTime * 0.005) * 0.5;
    float r = chladni(uv.x, uv.y, 2.4, 1., ts * 19., ts * 15.);
    float g = chladni(uv.x, uv.y, 1.4, 1., tc * 18.5, tc * 14.3);
    float b = chladni(uv.x, uv.y, 1.4, 1., ts * 17., tc * 13.);
    float level = sin(iTime + r) * 0.5;
    r *= sin(iTime) * 0.2;
    level *= 1. - (.1 + abs(uvl * .9));
    fragColor = vec4(r, g + r * 0.5, b, 1.) * level;
}
