}

#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Z .5
#define K0 1.
vec3 hash(vec3 p3)
    {
        p3 = fract(p3 * vec3(.1031, .1030, .0973));
        p3 += dot(p3, p3.yxz+33.33);
        return fract((p3.xxy + p3.yxx)*p3.zyx);

    }

// Translate Particles ðŸš—
Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-3.;x<=3.;x++)
    for (float y=-3.;y<=3.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
          if (b.w>0.) {
              vec4 a = A(U+xy);

              vec4 _1 = a, _2 = a;
              mat2 m = ei(6.2*hash(vec3(U+xy,iTime)).x);
              if (b.w>K0) {
                  vec2 v = vec2(0,Z)*m;
                  _2.xy -= v;
                  _1.xy += v;
              }
              if (_1.x<U.x+.5&&_1.x>=U.x-.5&&
                  _1.y<U.y+.5&&_1.y>=U.y-.5){
                  Q += .5*b.w*_1;
                  QB.w += .5*b.w; 
              }
              if (_2.x<U.x+.5&&_2.x>=U.x-.5&&
                  _2.y<U.y+.5&&_2.y>=U.y-.5){
                  Q += .5*b.w*_2;
                  QB.w += .5*b.w;
              }
         }
    }
    if (QB.w >0.) Q /= QB.w;
}

// Translate Particles ðŸš—
Main {
    Q = vec4(0);
    vec4 QB = vec4(0);
    for (float x=-3.;x<=3.;x++)
    for (float y=-3.;y<=3.;y++)
    {
          vec2 xy = vec2(x,y);
          vec4 b = B(U+xy);
          if (b.w>0.) {
              vec4 a = A(U+xy);

              vec4 _1 = a, _2 = a;
              mat2 m = ei(6.2*hash(vec3(U+xy,iTime)).x);
              if (b.w>K0) {
                  vec2 v = vec2(0,Z)*m;
                  _2.xy -= v;
                  _1.xy += v;
              }
              if (_1.x<U.x+.5&&_1.x>=U.x-.5&&
                  _1.y<U.y+.5&&_1.y>=U.y-.5){
                  Q += .5*b.w*_1;
                  QB.xyz += .5*b.w*b.xyz;
                  QB.w += .5*b.w; 
              }
              if (_2.x<U.x+.5&&_2.x>=U.x-.5&&
                  _2.y<U.y+.5&&_2.y>=U.y-.5){
                  Q += .5*b.w*_2;
                  QB.xyz += .5*b.w*b.xyz;
                  QB.w += .5*b.w;
              }
         }
    }
    Q = QB;
    if (QB.w>0.)Q.xyz/=QB.w;
}

// Force ðŸŒŠ
Main {
    Q = A(U);
    vec4 QB = B(U);
    vec2 f = vec2(0);
    if (QB.w>0.)
    {
        for (float x = -3.;x<=3.;x++)
        for (float y = -3.;y<=3.;y++)
        if (x!=0.||y!=0.)
        {
              vec2 xy = vec2(x,y);
              vec4 a = A(U+xy);
              vec4 b = B(U+xy);
              vec2 r = -xy;
              float l = length(r);
              if (l>0.) {
                  //f += .01*b.w*r/l*(15.*exp(-l*l)-exp(-l));
                  f -= .001*b.w*r/l/l*((QB.x*b.x+QB.y*b.y+QB.z*b.z)-b.w);

                  if (l>1.)
                  f += .05*b.w*(a.zw-Q.zw)/l/l;
              }
         }


        Q.zw += f.xy;
        Q.xy += Q.zw*3.*inversesqrt(
                1.+dot(Q.zw,Q.zw));
    }
    Q.w -= .0001;

    if (length(U-vec2(0,.5)*R)<10.) Q = vec4(U+vec2(1,1),+.1,.1);
    if (length(U-vec2(1,.5)*R)<10.) Q = vec4(U+vec2(-1,1),-.1,.1);


    //Q.zw *= exp(-.01*QB.x);
    //Q.zw *= 1.-.2*pow(C(U).x,1.);
     //      if (R.y-U.y<2.) Q = vec4(U-vec2(0,1),0,-1);     
    if (Q.x<2.)Q.z=abs(Q.z),Q.x=2.;
    if (Q.y<2.)Q.w=abs(Q.w),Q.y=2.;
    if (R.x-Q.x<3.)Q.z=-abs(Q.z),Q.x=R.x-3.;
    if (R.y-Q.y<3.)Q.w=-abs(Q.w),Q.y=R.y-3.;
    if (iMouse.z>0.&&length(U.xy-iMouse.xy)<20.)
        Q = vec4(U+vec2(.5,0),.25,0);

    if (iFrame < 1) 
        Q = vec4(U,0,0);
}

// Color ðŸŒˆ
Main {
    Q = B(U);
    vec4 QA = A(U);
    if (iMouse.z>0.&&length(U.xy-iMouse.xy)<20.)
        Q=vec4(1),
        Q.w = 1.;
    if (iFrame < 1) {
        Q = vec4(0);
        if (length(U-0.5*R)<20000.) Q = vec4(sign(sin(.01*U.x+vec3(1,2,3))),.5);
    }
    Q.xyz = sign(Q.xyz);
    if (U.x < 1.||U.y < 1.||R.x-U.x<1.||R.y-U.y<1.) Q = vec4(0);
    if (R.y-U.y<2.) Q = vec4(0);


    if (length(U-vec2(0,.5)*R)<10.) Q =  vec4(sign(sin(.1*iTime+vec3(1,2,3))),1);
    if (length(U-vec2(1,.5)*R)<10.) Q =  vec4(sign(sin(.1*iTime+vec3(1,2,3))),1);

}
