void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(
        cos(pow(-uv.x - .5, 2.) * 10. + iTime + 1.), // R
        cos(-pow(-uv.x - .8, 2.) * 10. + iTime * 1.),  // G
        cos(-pow(-uv.x - .2, 3.) * 10. + iTime / 3.),  // B
    1.)

    +  cos(uv.x * 100. + iTime * 3.) * vec4(.1, .5, .9, 1.);

}
