const float SPEED = 40.0;

vec3 makeColor(vec2 screenPos, float stripeWidth, float colorG, float colorB)
{
	float halfStripeWidth = stripeWidth * 0.5;
	vec2 offset = screenPos + vec2(1.0, 1.0) * iTime * SPEED;
	float offsetVal = offset.x + offset.y;
	float s = mod(floor(offsetVal / stripeWidth), 2.0) * 2.0 - 1.0;
	float a = ((s * (mod(offsetVal, stripeWidth) - halfStripeWidth)) / halfStripeWidth + 1.0) * 0.5;
	float v = smoothstep(0.0, 1.0, a);
	return 0.96 * vec3(v * sin(iTime) + 0.5, colorG, colorB);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec3 c1 = 0.5 * makeColor(fragCoord, 100.0, 0.3, 0.7);
	vec2 transposedFragCoord = vec2(fragCoord.x, iResolution.y - fragCoord.y);
	vec3 c2 = 0.5 * makeColor(transposedFragCoord, 20.0, sin(iTime), cos(iTime));
	fragColor = vec4(c1 + c2, 1);
}
