void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    const float radius = .1f;
    const float border = .05f;

    const float fakeMovementPeriod = 0.25f;
    uv.x += fakeMovementPeriod * sin(iTime);
    uv.y += fakeMovementPeriod * cos(iTime);


    float c = smoothstep( radius, radius-border , length(uv) );
    float g = smoothstep( radius, radius+border , length(uv) );
    g +=c;   

    fragColor = vec4(vec3(g),1.0f);
}
