void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy) / iResolution.y;
    uv.x -= 0.5;

    //uv.x -= (sin(iTime/2.0)+1.0) * 57.5;
    //uv.y += (sin(iTime/2.0)+1.0) * 676.3;

    //uv /= (sin(iTime/2.0)+1.0) * 700.0;


    float maxIterations = 1000.0;
    float iteration = 0.0;

    float a = uv.x;
    float b = uv.y;

    float ca = a;
    float cb = b;

    bool inBounds = true;

    for(int i=0; float(i)<maxIterations; i++){
        float real = a*a - b*b;
        float imag = 2.0 * a * b;

        a = real + ca;
        b = imag + cb;

        if(abs(a + b) > 100.0){
            iteration = float(i);
            inBounds = false;
            break;
        }
    }

    vec3 bright;
    vec3 col;

    if(inBounds){
        col = vec3(0.0);
    } else{
        bright = vec3(smoothstep(0.0, 0.6, iteration/100.0));
        bright = exp(1.0/1.6*bright)-1.0;

        col = vec3(bright.x, bright.y*2.0, bright.z*2.0);
        //col = 1.0-col;

    }

    // Output to screen
    fragColor = vec4(col ,1.0);
}
