#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.02,e; vec3 p, r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*e;
    p.z-=6.;
    p.zy*=R2D(iTime*.2);p.xz*=R2D(iTime*.2);

    for(j=0.;j++<16.;)
        p=abs(abs(p-1.)-.08)-.03,
        d=max(min(p.z,p.x),p.y),
        d-=dot(p,p)/1e4;

    e+=d;

}o+=vec4(exp(p),1)*3./i;
}
