void mainImage(out vec4 outColor, in vec2 inColor) {
  vec2 uv = (2.0 * inColor.xy - iResolution.xy) / iResolution.y;

  float angle = atan(uv.y, uv.x);
  float dist = length(uv);

  float inc = 1.0 + sin(iTime * 0.33) * 0.25;
  float swirl = 2.0 + sin(iTime * 0.66) * 2.0;

  outColor = 0.5 + sin(iTime + angle + vec4(1.0, 2.0, 3.0, 0.0) * inc + sin(dist * swirl)) / 2.0;

  float scale = 2.0 - pow(0.75 / dist, sin(iTime) * 20.0);
  outColor *= vec4(scale);

  float power = 0.5;
  outColor = pow(outColor, vec4(power));

  outColor = mix(outColor, tanh(outColor), 0.5);
}
