mat2 spin(float angle){
    return mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // limit rendering to the lower left of the screen
    const float size = 258.0;    
    if (fragCoord.x > size || fragCoord.y > size)
    {
        fragColor = vec4(0.0);
        return;
    }

    // calculate uv
	vec2 uv = fragCoord/size;    

    // shift uv space so texture spins around origin
    uv -= 0.5;

    // sample texture
    fragColor = texture(iChannel0,spin(iTime)*uv);
}
