float circleSDF(vec2 pnt, float r)
{
    return length(pnt) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0f * fragCoord - iResolution.xy) / iResolution.y;

    vec2 center = vec2(0.0f);
    //    center = vec2(sin(iTime), 0);

    float d = circleSDF(p - center, 0.5);

    vec3 black = vec3(0.0);
    vec3 white = vec3(1.0);
    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 blue = vec3(0.65, 0.85, 1.0);
    vec3 orange = vec3(0.96, 0.7, 0.8);
    vec3 col = d > 0.0 ? orange : orange*3.0;
    //    col *= exp(d);
    if(d>0.0f)
   col *= (exp(-2.0f * abs(d)));

    fragColor = vec4(col, 1.0f);
}
