float circleSDF(vec2 p, float r)
{
    return length(p) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 orange = vec3(250.0f, 208.0f, 0.0f) / 255.0f;
    vec3 red = vec3(1.0f, 0.0f, 0.0f);
    vec2 p = (2.0f * fragCoord - iResolution.xy) / iResolution.y;

    vec2 center = vec2(0.0f);
    float d = circleSDF(p, 0.5);

    vec3 color;

    color = d > 0.0f ? red : orange;
    color *= (1.0f - exp(-10.0f * abs(d)));

    color = 0.8 * color + 0.2 * sin(150.0f * d + 8.0f * iTime) * color;
    fragColor = vec4(color, 1.0f);
}
