float D = 1.,
     DT =.1,
      A =.75;

void mainImage(out vec4 O, vec2 p) {

    O = T(p,);
    O +=  DT * (   D * ( T( p, 1. ) - T( p, ) ) // ~ Laplace(p) 
                 + O * ( 1.-O.r-O.g-O.b  - A * O.yzxw )
               );        // rho

    if (iFrame < 3 || (length(p-iMouse.xy) < 20. && iMouse.z > 0.) ) // mouse paint
        O = texelFetch(iChannel1, ivec2(p+iTime) %256, 0 ); 

}
