vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {
	if( length(r-center) < radius) {
		pixel = color; 
	}
}

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos(6.283185*(c*t+d) );
}

void ring(vec2 r, vec2 center, float radius, vec3 color, float thick, inout vec3 pixel){
    if(length(r-center) < radius){   
		pixel = color;        
	}
}

vec4 circle(vec2 uv, float r) {
  float x = uv.x;
  float y = uv.y;

  float d = length(vec2(x, y)) - r;

  if (d < 0.0) {
      return vec4(0.0);
  }
  else {
   return vec4(0.9, 0.9, 0.97, 1.0);
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - 0.5)*2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 center = iResolution.xy * 0.5;
	float radius = 0.25 * iResolution.y;

    vec4 circle = circle (uv, 0.7);

    float d = length(uv);

    vec3 colorone = vec3(0.9,0.9,0.11);
    vec3 colortwo = vec3(0.1,0.4,0.2);
    vec3 colorthree = vec3(2.0,1.0,1.0);
    vec3 colorfour = vec3(0.0,0.25,0.25);

    vec3 col = palette( d + iTime, colorone,colortwo,colorthree,colorfour);

   //d -= 0.5;
    d = sin(d*8.0 - iTime)/8.0;
    d = abs(d);
    d = 0.03/d;

    col *=d;

    float ringRadius = 0.5; 
    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    vec3 pixel = vec3(0.1);

    vec3 col1 = 0.5 - 0.5*cos(iTime+uv.xyx+vec3(4,1,1));
    vec3 col2 = vec3(rgb(255.0,255.0,0.0));

    vec2 move = vec2((cos(iTime)*ringRadius),(sin(iTime)*ringRadius));

    ring(r, vec2(0.0), 0.7, col2, 0.7, pixel);
    disk(r, move, 0.2, col1, pixel);

    if (circle.r <= 0.0) {
        fragColor = vec4(pixel,1.0);
    }
    else {
        fragColor = vec4(pixel,1.0)+vec4(col,1.0);
    }
}
