// Thanks https://www.shadertoy.com/view/ttc3zr
uint murmurHash13(uvec3 src) {
    const uint M = 0x5bd1e995u;
    uint h = 1190494759u;
    src *= M; src ^= src>>24u; src *= M;
    h *= M; h ^= src.x; h *= M; h ^= src.y; h *= M; h ^= src.z;
    h ^= h>>13u; h *= M; h ^= h>>15u;
    return h;
}

// 1 output, 3 inputs
float hash13(vec3 src) {
    uint h = murmurHash13(floatBitsToUint(src));
    return uintBitsToFloat(h & 0x007fffffu | 0x3f800000u) - 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    //float time = iTime * 1000.0;
    float time = 51.384;
    // Time varying pixel color
    float h = hash13(vec3(uv, time));
    //h = h > 0.5 ? 1.0 : 0.0;
    vec3 rgb = texture(iChannel0,uv).rgb;
    if (rgb.g > 0.6 && (rgb.b + rgb.r) < 0.3) h = 1.0-h;
    //if (rgb.g > 0.5) h = 1.0-h;

    // Output to screen
    fragColor = vec4(h,h,h,1.0);
}
