void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;//中心移动到0.5，0.5的中心
    uv.x *= iResolution.x / iResolution.y;//屏幕的x,y轴要比例一致


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float left = -0.2;//黑色条纹左边界
    float right = 0.5;//黑色色条纹右边界
    float up = 0.2;//黑色条纹上边界
    col*= 1.0-((smoothstep(left-0.01,left+0.01,uv.x) - smoothstep(right-0.01,right+0.01,uv.x)) * (1.0 - smoothstep(up-0.01,up+0.01,uv.y)));

    // Output to screen
    fragColor = vec4(col,1.0);


    // Output to screen
    fragColor = vec4(col,1.0);
}
