void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float scale = 3.0;
    vec2 p = (fragCoord + vec2(iFrame/2, iFrame/4)) / scale;
    int x = int(p.x);
    int y = int(p.y);

    int v = 1;
    //v = (x^y)%(int(iFrame / 45) % 40);
    v = (x^y)%(3);
    //v = (x^y)%(11);
    //v = (x*y)%(2*3*4);
    //v = (x*y)&(64);
    //v = (x&y);

    // Output to screen
    float v1 = v > 0 ? 1.0 : 0.0;
    fragColor = vec4(v1, v1, v1 ,1.0);
}
