float norm(vec2 p, float k) {
    p = abs(p);
    return pow(pow(p.x,k)+pow(p.y,k),1./k);
}

float getSound(float i) {
    return texture( iChannel0, vec2( 0.05 + 0.5*i/16.0, 0.25 ) ).x;
}

vec3 palette(float t) {
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28 * c * (t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;



    float t = mod(iTime, 7200.) * .5;

    vec3 outCol = vec3(0.);
    for (float i=1.; i<=2.; i+=.5) {

        vec2 uvg = uv * (3. + i) + i ;
        uvg = fract(uvg) - 0.5;

        vec3 col = palette(t);

        float dist = abs(sin(9. * norm(uvg, 2.) + iTime + getSound(1.)*10.));
        float contour = smoothstep(0.05, -0.05, abs(dist - 0.2) - 0.1);

        outCol += col * contour * (2.-i);
    }

    // Output to screen
    fragColor = vec4(outCol,1.0);
}
