float sdScene(vec3 p, bool vol) {
    float d = sdPlane(p);
    if(vol) {
        float s2 = .3;
        vec3 p2 = p - s2 * round(p / s2);
        d = min(d, sdSphere(p2, 1e-2));
    }
    float s = 2.;
    p.xz = p.xz - s * round(p.xz / s);
    d = min(d, sdSphere(p - vec3(0, .3, 0.), 0.3));
    return d;
}

vec3 calcNormal(vec3 p) {
    const float eps = 1e-4;
    vec3 h = vec3(eps, 0.0, 0.0);
    return normalize(vec3(
        sdScene(p + h.xyy, false) - sdScene(p - h.xyy, false),
        sdScene(p + h.yxy, false) - sdScene(p - h.yxy, false),
        sdScene(p + h.yyx, false) - sdScene(p - h.yyx, false)
    ));
}


vec3 rayMarch(vec3 ro, vec3 rd) {
    vec3 pos;
    float z = 0.;
    for(int i = 0; i < 2048 && z < 1e4; i++) {
        pos = ro + z * rd;
        float d = sdScene(pos, false);
        if(d < 1e-4) {
            return pos;
        }
        z += d;
    }
    return vec3(0.);
}

float notShadow(vec3 ro, vec3 rd, vec3 target) {
    float z = 0.;
    for(int i = 0; i < 2048 && z < 1e4; i++) {
        vec3 pos = ro + z * rd;
        float d = sdScene(pos, true);
        if(length(pos - target) < 1e-2) return 1.;
        if(d < 1e-4) break;
        z += d;
    }
    return 0.3;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0., .6, 1.5);
    ro *= rotationX(-0.4) * rotationY(iMouse.x / 100.);
    vec3 rd = normalize(vec3(uv, -1.));
    rd *= rotationX(-0.4) * rotationY(iMouse.x / 100.);

    float t = iTime / 3.;
    vec3 lightPos = vec3(cos(t), 1., sin(t));

    vec4 c = vec4(0);

    vec3 pos = rayMarch(ro, rd);

    if(length(pos) != 0.) {
        vec3 normal = calcNormal(pos);
        c.rgb = (normal + 1.) / 2.;
        vec3 lightDir = normalize(lightPos - pos);
        c.rgb *= max(.3, lambert(pos, normal, lightDir));
        c.rgb *= notShadow(lightPos, -lightDir, pos);
    }

    fragColor = c;
}

#define PI 3.1415926535897932384626433832795

float lambert(
  vec3 pos, vec3 normal, vec3 lightDir
) {
    return max(dot(normal, lightDir), 0.);
}

float sdPlane(vec3 p) {
    return p.y;
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}


mat3 rotationY(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(
     c, 0.,  s,
    0., 1., 0.,
    -s, 0.,  c
  );
}

mat3 rotationX(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(
    1., 0., .0,
    0.,  c, -s,
    0.,  s,  c
  );
}

mat3 rotationZ(float angle) {
  float c = cos(angle);
  float s = sin(angle);
  return mat3(
     c, -s, 0.,
     s,  c, 0.,
    0., 0., 1.
  );
}

mat3 rotation(vec3 angles) {
  float cx = cos(angles.x);
  float sx = sin(angles.x);
  float cy = cos(angles.y);
  float sy = sin(angles.y);
  float cz = cos(angles.z);
  float sz = sin(angles.z);

  mat3 rotX = mat3(
    1., 0., 0.,
    0., cx, -sx,
    0., sx, cx
  );

  mat3 rotY = mat3(
    cy, 0., sy,
    0., 1., 0.,
    -sy, 0., cy
  );

  mat3 rotZ = mat3(
    cz, -sz, 0.,
    sz, cz, 0.,
    0., 0., 1.
  );

  return rotZ * rotY * rotX;
}

