// Fork of "ADHD Pulsar with Jets" by izutionix. https://shadertoy.com/view/lX2yDG
// 2024-10-08 10:01:00

const float PI = 3.1415926535898;

vec3 screen(vec3 a, vec3 b) {
    return a+b - a*b;
}

vec3 glow(float gradient, vec3 color, float intensity, vec3 offset) {
    return exp(-intensity * color * (gradient - offset));
}

vec3 jets(vec2 uv, vec3 color) {
    // polar coordinates    
    float a = atan( uv.y, uv.x );
    float r = length( uv );
    vec2 puv = vec2(a, r);

    float width = .1;

    float amplitude = 0.7;
    float frequency = 40.;

    vec3 col = .5 + .5 *( cos(.5*color + PI + puv.x*2. + amplitude * cos(frequency * iTime))-(1.-width) ) / width;
    col = clamp(col, 0., 1.);
    col *= 2.*r;

    return col;
}

vec3 star(vec2 uv, vec3 color) {
    float f = length(uv);

    float intensity = 10. * (1.5+cos(67.*iTime));
    vec3 offset = vec3(0.1);

    vec3 col = glow(f, color, intensity, offset);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 color = vec3(1., 0.5, 0.25);
    vec3 col = screen( jets(uv, color), star(uv, color) );

    fragColor = vec4(col, 1);
}

vec4 someFunction( vec4 a, float b )
{
    return a+b;
}

vec2 mainSound( int samp, float time )
{
    return vec2( 0.*sin(6.2831*220.0*time)*sin(-300.0*time) );
}
