#define I iResolution
#define PI 3.1415926
#define T( s ) fract( iTime * s ) * PI * 4.
#define rot( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )
#define v( a ) clamp( a, 0., 1. )

// photoshop blend mode
// https://www.shadertoy.com/view/XdS3RW
float hardLight( float s, float d )
{
	return (s < 0.5) ? 2.0 * s * d : 1.0 - 2.0 * (1.0 - s) * (1.0 - d);
}

float repx( inout float p, float size, float count ){

    float cell = round( p / -size );

    p = p - size * clamp(round(p/size),-count,count);

    return cell;
}

float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b, a, h) - r*h*(1.-h);
}

float smax(float a, float b, float k) {
    return -smin(-a, -b, k);
}

float f( vec3 p, float r ){
    return smax(
        length( p.zy * vec2( 1., 2. * abs( p.y ) ) ) - r,
        abs( p.x ) - .03,
        .008
    );
}

float bg( vec3 p ){
    float x = repx( p.x, .5, 5. );

    return 1. - f(p, .3);
}


float df( vec3 p ){

    float x = repx( p.x, .06, 40. );

    p.yz *= rot( ( ( x + 40. ) / 15. ) - iTime );
    p.y += cos( iTime ) * .3;

    return f( p, .3 );

}



vec3 l(in vec3 b) {
  vec2 a = vec2(1, -1) * .5773;
  return normalize(a.xyy * df(b + a.xyy * 5e-4) + a.yyx * df(b + a.yyx * 5e-4) +
                   a.yxy * df(b + a.yxy * 5e-4) + a.xxx * df(b + a.xxx * 5e-4));
}

float S( vec3 p, vec3 ca, vec3 r, float q ){
    return v( pow( dot( l( p ), normalize( normalize( ca ) - r ) ), q ) );
}

vec3 ref( vec3 r ){
    vec3 o = vec3( 0., 0., -1. ), c, p;
    float t, d, i;
    for( ; i < 32.; i++ )
        p = o + r * t,
        d = bg( p ),
        t += d * .3;

    if( d < 1e-3 ){
        float dd = v( dot( l(p), vec3( .5, 1., -1. ) ) );
        c = vec3( .2 * dd );
    }

    return c;
}

// iY0Yi's glsl covted
// https://www.shadertoy.com/view/3ddBDn
vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    float A = min( I.x, I.y );
    vec2 u = ( V * 2. - I.xy ) / A;

    u *= rot( I.x > I.y ? PI * .0 : PI * .5 );

    vec3 c, p, o = vec3( 0., 0., -1. ), r = vec3( u, 1. );
    float t, d, i;
    for( ; i < 64.; i++ )
        p = o + r * t,  
        d = df( p ),
        t += d * .3;

    if( d < 1e-3 ){

        vec3 lg = vec3( -.1 + cos( iTime ), .3 + sin( iTime ), -.7 ) * vec3( 0.85, 0.85, 0.82 );
        float dd = v( dot( l( p ), lg ) );
        dd = pow( dd, 4. );
        c = mix(
            c,
            vec3( .6, .91, .37 ) * .4,
            hardLight( S( p, lg, r, 38. ), dd )
        );

        lg = vec3( -.2, .5 + cos( iTime ), -.5 + sin( iTime ) ) * vec3(0.329,0.850,0.205);
        dd = v( dot( l( p ), lg ) );
        c += S( p, lg, r, 14. ) * dd * vec3(0.109,0.089,0.630);

        lg = vec3( .2 - cos( iTime ), -.6, -.7 ) * vec3( 0.55, 0.85, 0.45 );
        dd = v( dot( l( p ), lg ) );
        c += S( p, lg, r, 18. ) * dd * vec3(1.100,0.239,0.644);     

        c = ACESFilm( c );

        c -= ref( ( reflect( l( p ), r ) ) ) * .25;

    }

    // Output to screen
    U = vec4(c,1.0);
}
