void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // 获取屏幕中心坐标
    vec2 center = iResolution.xy / 2.0;
    // 初始化颜色为黑色
    fragColor = vec4(1.0, 0.0, 1.0, 0.0);
    // 循环生成五个彩色圆
    for (float i = 0.0; i < 10.0; i++)
    {
        // 计算当前圆的半径和位置
        float radius = mix(70.0, 25.0, sin(iTime + i * 0.5));
        vec2 circleCenter = center + vec2(sin(iTime + i * 0.6), cos(iTime + i * 0.6)) * 190.0;
        // 计算当前像素到圆心的距离
        float distance = length(fragCoord - circleCenter);
        // 判断当前像素是否在圆内
        if (distance < radius)
        {
            // 在圆内的颜色为白色
            fragColor = vec4(1.0, 1.0, 1.0, 1.0);
            break; // 只显示最接近的圆，避免重叠
        }
    }
}
