#ifdef GL_ES
precision mediump float;
#endif

#define PI 3.14159265359


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.0)-_st);
    return uv.x*uv.y;
}

float crossBox(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
/*
    st -= vec2(0.5);
    st = rotate2d( sin(iTime)*PI ) * st;
    st = scale( vec2(sin(iTime)+1.0) ) * st;
    st += vec2(0.5);


    color = vec3(st.x,st.y,0.0);

    color += vec3(crossBox(st,0.1));

*/


    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.0);
    st = (st - 0.5) * aspect + 0.5;


    int numCrosses = 20;
    for (int i = 0; i < numCrosses; i++) {
        for (int j = 0; j < numCrosses; j++) {
            vec2 gridPos = vec2(float(i) / float(numCrosses)-0.5, float(j) / float(numCrosses)-0.5);
            vec2 pos = st - gridPos;


            pos -= vec2(0.5);
            pos = rotate2d( sin(iTime + float(i + j) * 0.1) * PI ) * pos;
            pos = scale( vec2(sin(iTime + float(i + j) * 0.1) + 1.0) ) * pos;
            pos += vec2(0.5);


            color += vec3(crossBox(pos, 0.1));
        }
    }


    fragColor = vec4(color,1.0);
}
