void mainImage( out vec4 o, in vec2 i)
{
  vec2 uv = i/iResolution.xy - 0.5;
  uv.x = uv.x * iResolution.x/iResolution.y;
  uv *=1. - 0.6*cos(iTime);
  uv.x += 0.5*sin(iTime);
  uv.y += 0.5*sin(iTime);

  float col = smoothstep(0.4,0.4,abs(uv.x) + abs(uv.y));

  o = vec4(col*vec3(0.,1.,0.)+col*vec3(0.,0.,1.),1.);
}
