#define PI 3.14159

vec3 pal(float t) {
    return .5+.5*cos(2.*PI*t*vec3(4,2.11,.45));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    float r = length(uv)/sqrt(3.);
    float a = atan(uv.x, uv.y);

    float aFac = 8.;
    float rFac = 8.;//mix(8., 10., cos(iTime*.25)*.5+.5);

    float a2 = cos(a*aFac - iTime)*.5+.5;
    float r2 = cos(r*rFac - iTime)*.5+.25;

    a2 = sin(a2*PI)*.5+.5;

    float aFac2 = PI;
    float rFac2 = PI*mix(1., 2.5, cos(PI+iTime*.5)*.5+.5);    

    float t = cos(r2*rFac2 + a2*aFac2 - iTime + PI)*.5+.5;
    vec3 col = pal(t + r);

    fragColor.xyz = pow(col, vec3(.4545));
}
