#define PI 3.14159265

float changeSignPeriodically(float f, float signChangeFreqInSeconds) {
   return f * sign(signChangeFreqInSeconds - mod(iTime, 2. * signChangeFreqInSeconds));
}

float digitalize(float normalizedValue) {
    return ceil(normalizedValue) + floor(normalizedValue);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float numBands = 9.;
    float signChangeFreqInSeconds = 1.;

    vec2 st = fragCoord/iResolution.xy;
    float x = st.x;

    float sinValue = sin(PI * x * numBands);
    float digitalSinValue = digitalize(sinValue);
    float intensity = changeSignPeriodically(digitalSinValue, signChangeFreqInSeconds);

    vec3 color = vec3(intensity, intensity, intensity);

    fragColor =  vec4(color, 1.0);
}
