#define R iResolution.xy
void mainImage(out vec4 O,vec2 F)
{
    F=(F-R*.5)/min(R.x,R.y)/.5;
    O=vec4(.5+.5*dot(vec2(F.x,sqrt(1.-F.x*F.x)),vec2(.7)));
}
