float N(vec2 p){
    return fract(sin(p.x * 100.624 + p.y * 6574.863) * 5647.234);
}

void mainImage( out vec4 o, vec2 i )
{
    float grid = 10.0;
    vec2 uv = i/iResolution.y * grid;
    vec2 uvCell = fract(uv);
    vec2 uvID = (uv - uvCell) / grid;

    float n = N(uvID);
    float m = n * (sin(iTime*n)) * 4.0;
    vec2 uvPixel = floor(uvCell * m)/m;
    uvCell = abs(uvCell * 2.0 - 1.0);
    float h = max(uvCell.x, uvCell.y);
    h = 1.0 - abs(h - 0.5);
    n = mix(uvPixel.x * uvPixel.y, length(uvPixel), 0.5);
    n = n * h;
    o = vec4(h-n);
}
