void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvCols = fract(uv * 5.0);
    float columns = (step(uvCols.x,0.5));



    uv.y +=columns * iTime/10.0;
    uv.y -= (1.0-columns) * iTime/10.0;

    vec2 uvcCircles = fract(uv * 10.0);

    uvcCircles -= 0.5;
    uvcCircles.x *= iResolution.x/iResolution.y;

    float dist = length(uvcCircles);
    vec3 color = vec3(step(dist,0.25));

    // Output to screen
    fragColor = vec4(color,0);

    //fragColor = vec4(columns);

}
