//play around with these values
const float modVal = 2.0;
const int iMax = 10;
const float scale = 5.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = scale*(2. * fragCoord - iResolution.xy)/iResolution.y;
    vec2 uvMouse = (2.*iMouse.xy - iResolution.xy)/iResolution.y;
    mat2 skew = mat2(1.0,uvMouse,1.0);

    for(int i;i<iMax;i++)
    {
        uv *= skew;
        uv = mod(uv,modVal);
    }

    vec3 tint = vec3(0.2,0.25,0.5);
    vec3 col = tint * vec3(exp(1.0*uv.xyx) + 1.0);
    fragColor = vec4(col,1.0);
}
