#define h21(a) fract(1e3 * sin(dot(a, r)))
#define pal(a) (.8 + h21(a+.1) / 3.6 * cos(2.* pi * h21(a) + vec4(0, 23, 21, 0)))
#define pi acos(-1.)

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (2. + cos(iTime * .1)) * (u + u - r) / r.y;

    if(cos(iTime) > .5) //
        u = vec2(
                log(dot(u, u)),
                atan(u.x, u.y) * 6. / pi
            );

    vec2 k = floor(u);

    u += 
        tanh(
            sin(
                iTime * .5 + u.yx * pi
            ) * (h21(k) -  .25)
        );

    o.zw = mod(floor(u), 12.);

    o.xy = fract(u) - .5 
             + cos(
                   h21(k + 1.) * 2. * pi 
                   + iTime * 2. 
                   + vec2(0,11)
               ) * .1;

    float g = length(
                  tanh(
                      cos(atan(o.x, o.y) * 12.) * 4.
                  )
              ) * .1;

    o.x = .5 - length(o.xy);
    o.x = max(o.x, 0.);
    o = 
        pow(o.x 
            - g                           // grid
            + pow(o.x + .4, 5.) * 1.,     // spec
            .3                            // gama
        ) * pal(o.zw);                    // alb
}
