void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;

    // Scale and shift UV coordinates for Nyan Cat
    uv *= 2.;
    uv -= vec2(2.5, 0.1);

    // Move the cat horizontally based on iTime
    float speed = -0.5; 
    uv.x += mod(iTime * speed, 4.0) - 0.9;  

    uv = clamp(uv, 0., 1.);

    float frame = floor(mod(iTime * 10.0, 6.0));

    float nyanWidthUV = 40.0 / 256.0;
    uv.x = (uv.x + frame) * nyanWidthUV;

    vec4 nyan = texture(iChannel0, uv);

    vec2 center = vec2(iResolution.x * 0.5, iResolution.y * 0.1);  // Set the rainbow center slightly lower
    vec2 coordFromCenter = fragCoord.xy - center;

    // Compute polar coordinates (angle and radius)
    float angle = atan(coordFromCenter.y, coordFromCenter.x);
    float radius = length(coordFromCenter) / iResolution.y;

    vec3 rainbowColor = vec3(0.0);

    float minR = 0.6;
    float maxR = 1.;
    float interval = (maxR - minR) / 7.;

    if (radius > minR && radius < maxR) {
        // Assign rainbow colors based on the radius
        if (radius < minR + interval) {
            rainbowColor = vec3(0.6, 0.2, 1.0);  // Purple
        } else if (radius < minR + interval * 2.) {
            rainbowColor = vec3(0.1, 0.3, 0.8);  // Blue
        } else if (radius < minR + interval * 3.) {
            rainbowColor = vec3(0.0, 1.0, 1.0);  // Cyan
        } else if (radius < minR + interval * 4.) {
            rainbowColor = vec3(0.0, 1.0, 0.0);  // Green
        } else if (radius < minR + interval * 5.) {
            rainbowColor = vec3(1.0, 1.0, 0.0);  // Yellow
        } else if (radius < minR + interval * 6.) {
            rainbowColor = vec3(1.0, 0.5, 0.0);  // Orange
        } else {
            rainbowColor = vec3(1.0, 0.0, 0.0);  // Red
        }
    }


    vec3 groundColor = vec3(0.3, 0.6, 0.3);

    rainbowColor = mix(rainbowColor, nyan.rgb, nyan.a);  
    groundColor = mix(groundColor, nyan.rgb, nyan.a);  

    if (fragCoord.y < iResolution.y * 0.25) {
        // Set ground color if below ground level
        fragColor = vec4(groundColor, 1.0);  
    } else {
        // Set rainbow and Nyan Cat above the ground
        fragColor = vec4(rainbowColor, 1.0);  
    }
}
