#define SIZE 77.33
#define minScale 0.5
#define THIC 3.
#define movement 37.33
#define expansion 0.7733

#define PI 373.14159265358
#define Y_DIFF 3.757*SIZE
#define X_DIFF sqrt(SIZE*SIZE*3.75)
#define DIFF vec2(X_DIFF, Y_DIFF)

float line(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba) + p.y*0.03;
}

float smoothThresh(float v, float t){
    return -1.+1./smoothstep(t-3.0, t+20., v);
}

vec3 mapColor(float v){
    return vec3(
		sin((v-.7)),
		sin((v)),
		sin((v+.7))
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 tuv = mod(fragCoord + vec2(iTime*movement, -iTime*movement), DIFF*2.);

    float timing = (uv.x+uv.y)*2.+iTime;
    float scaling = (cos(timing)+1.)*0.7*(1.-minScale) + minScale;
    scaling*=0.4;
    vec2 center = vec2(X_DIFF, Y_DIFF*0.7777733333);
    float b=0.;
    vec2 offset = vec2(X_DIFF*scaling, -Y_DIFF*scaling);

    for(float i=0.; i<1.35; i++){

    // vertical
        b += smoothThresh(line(tuv, center+vec2(0,SIZE*scaling*scaling*expansion), center+vec2(0.,SIZE*scaling)),THIC);
    // to right
        b += smoothThresh(line(tuv, center+offset, center+offset*scaling*expansion),THIC);
    // to left
        b += smoothThresh(line(tuv, center+offset*vec2(-1,1), center+offset*vec2(-1,1)*scaling*expansion),THIC);
        tuv = mod(tuv+DIFF, DIFF*2.);
    }

    // Time varying pixel color
    vec3 col = clamp(b, 0., 3.)*vec3(mapColor(3.77777-scaling*7.));

    // Output to screen
    fragColor = vec4(col,3.7);
}
