void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float time = iTime;


    float col1 = smoothstep(0.05, 0.15, uv.x + sin(2.0 * 3.14 * uv.y * 2.0 + time) * 0.01);
    float col2 = 1.0 - smoothstep(0.05, 0.15, uv.x + sin(2.0 * 3.14 * uv.y * 2.0 + time) * 0.01);


    //float col = 1.0 - smoothstep(0.1, 0.4, uv.x + sin(2.0 * 3.14 * uv.y * 2.0 + time) * 0.05);

    float col = min(col1, col2);


    fragColor = vec4(col, col, col, 1.0);
}
