renderpass.code:
#define t iTime
//#define t -PI/6.
//#define t PI/6.
#define PI acos(-1.)
#define TAU (2. * PI)
#define TRAU 3. * PI / 2.
#define r iResolution.xy

vec2 tri(vec2 p) {
    float d = atan(p.y, p.x),
          k = 3.; // triang

    d = cos((mod(k * (d + t) + PI, TAU) - PI) / k);
    return vec2(
               smoothstep(9. / r.y, 0., abs(length(p) - 1.)), 
               smoothstep(0., 9. / r.y, 
                   .5 - d * length(p))); // cos(pi/3) = .5
}

void mainImage(out vec4 o, vec2 u) {
    o *= 0.;
    u = 4. * (u - r / 2.) / r.y;
    u.y += 1.;

    o.bg += tri(u);

    float h = 2. * PI / 3.,
          g = 3. / TAU;

    //u.y -= cos((mod(t * 3. + 3. * PI / 2., TAU) - PI) / 3.)  *  2.;
    u.y -= cos(fract(t * g + .75) * h - h / 2.) * 2.;


    u.y = -u.y;
    o.rg += tri(u);
}
