//Click on iChannel0 > Textures > Page 2 > London 
// Alt + Enter to Apply changes
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // get pixel information from uv location
    vec4 texColor = texture(iChannel0, uv);




    // Sample intensity value (for demonstration purposes, replace with your own logic)
    float intensity = fract(texColor.x * texColor.y * 2.5);

    vec4 color;
    if (intensity > 0.8)
        color = vec4(texColor.rgb, 1.0);
    else if (intensity > 0.5)
        color = vec4(texColor.rgb, 1.0);
    else if (intensity > 0.25)
        color = vec4(0.2, 0.3, 0.3, 1.0);
    else
        color = vec4(0.2, 0.1, 0.1, 1.0);

    fragColor = color;
}
