float NUM_CIRCLES = 5.0;

float random(vec2 st)
{
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

float circle(vec2 uv, vec2 center, float r, float k)
{
    return 1.0 - smoothstep(r, r + k, length(uv + center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y + 0.5;
    float c = 0.0;
    vec4 rgb = vec4(1.0);

    for (float i = 0.0; i < NUM_CIRCLES; i++)
    {
        float r = random(floor(uv.xy * 5.0) + i * 3.0) * 0.3 + 0.05;
        float x = sin(iTime + i * 10.0 * r * 400.0);
        float y = cos(iTime + i * 50.0 * r * 100.0);

        c += circle(fract(uv * 5.0), vec2(x * r - 0.5, y * r - 0.5), 0.01, 0.2 * r * 2.0 + 0.1);
        rgb += vec4((x+0.25), (y+0.25), r, 0.0) * smoothstep(1.0, 0.9, c);
    }

    rgb /= NUM_CIRCLES;
    rgb = rgb * rgb * rgb;


    float color = smoothstep(0.3, 0.4, c);
    fragColor = rgb * color;
}
