const int f = 7;
const float ff = float(f);
const vec2 pixels = vec2(ff,ff)*20.;

const float hz = 60.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    ivec2 ij = ivec2(floor(uv * pixels));

    int i = ij.x; int j = ij.y;

    float t = ff*ff*ff*2.+iTime*hz;

    int a = int(t/(ff)) %f;
    int b = int(t/(ff*ff)) %f;
    int c = int(t/(ff*ff*ff)) %f;
    int d = int(t/(ff*ff*ff*ff)) %f;
    int e = int(t/(ff*ff*ff*ff*ff)) %f;



    bool cond = (a*i+b*j+c*i*j+d*i*i+e*j*j) % f == 0;


    float bright = 0.;
    if(cond){bright=1.;}

    // Output to screen
    fragColor = vec4(vec3(bright),1.0);
    }
