void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    float b = 0.0;

    for(float i=0.0; i<9.0; i++ ){

        float scale = 20.0 + i * 5.0;



        vec2 uv = fragCoord/iResolution.xy + i*0.123;




        vec4 dsr = texture( iChannel0, uv/20.0);
        uv.x += dsr.a*0.01 ;

        vec4 pos = texture( iChannel0, vec2(floor(uv.x*scale-0.5)/scale, 0));



        uv.y += iTime*(0.01-scale*0.00001 + pos.a*0.05);




        vec4 col = texture( iChannel0, floor(uv*scale-0.5)/scale);




        col.x = mix(col.x, col.z, 0.5+0.5*(sin(iTime*0.654)));
        col.y = mix(col.y, col.w, 0.5+0.5*(sin(iTime*0.721)));

        uv.x = uv.x + (col.x*1.0 - 0.5)/scale;
        uv.y = uv.y + (col.y*1.0 - 0.5)/scale;


        float a = 3.0 * abs(fract(uv.x*scale)-0.5) * abs(fract(uv.y*scale)-0.5);

        b = max(b, smoothstep(0.6, 0.8, a) * smoothstep( 0.701, 0.702, col.z));

    //b = col.x;

    }

    // Output to screen
    fragColor = vec4(b, b, b, 1.0);
}
