#define t   iTime
#define R   iResolution
#define pi  3.14159265359
#define tau 2.*pi

#define d(f,s) (2. * f.xy - R.xy) / R.y * s
#define r2(a)  mat2(cos(a), -sin(a), sin(a), cos(a))
#define hue(v) ( .6 + .6 * cos( 6.3*(v) + vec3(0,23,21) ) )

void mainImage(out vec4 c, in vec2 uv) {
    vec2  p  = d(uv, 1.);
    p *= r2(t*.1);
    p = fract(p) - .5;

    float a  = atan(p.y, p.x);
    float d = length(p) - .01;
    d = abs(d);

    float dfm5 = .1*fract(a / pi * (2.+mod(t, 10.)));
    d += dfm5;

    float e = abs(sin(t))*.5 + .2;
    d = smoothstep(.01, e, d);

    vec3 cl = vec3(0.);
    vec3 cp = hue(d); 
    cl += (1.-d)*cp*1.5;

    c = vec4(cl, 1.0);
}
