const vec2 TOTAL = vec2(10.0);
const vec3 COL1 = vec3(0.12, 0.8, 0.12);
const vec3 COL2 = vec3(0.92);

float Checkerboard(vec2 uv, vec2 total)
{
    uv = floor(total * uv);
	return mod(uv.x + uv.y, 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Aspect ratio corrected UV coordinates
    vec2 uv = fragCoord / min(iResolution.x, iResolution.y);

    // Checkerboard
    float c = Checkerboard(uv, TOTAL);

    // Combine colours
    vec3 result = mix(COL1, COL2, c);

    // Output to screen
    fragColor = vec4(result, 1.0);
}
