float pi = 3.1415926535897932384626433832795;
float loop = 5.;//more detail in fractal
vec3 objec(vec3 u)
{
    vec3 n = u;
         n = cos(iTime*vec3(.27,.71,.51)+vec3(0,1,3))*.8; //comment to show 3D mandelbrot
    for(float i = .5; i < loop; ++i)
    {
        u = vec3(atan(u.y,u.x),
                 atan(u.z,length(u.xy)),
                 log(length(u)));    u = u*2.;
        u = vec3(cos(u.y)*cos(u.x),
                 cos(u.y)*sin(u.x),
                 sin(u.y))*exp(u.z);   u = u+n;
    }
    return u;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camDir = texture( iChannel0, (vec2(1.,0.)+.5)/iResolution.xy).xyz;
    vec3 camPos = texture( iChannel0, (vec2(2.,0.)+.5)/iResolution.xy).xyz;
         camPos+= vec3(1.5,0,1.5);

	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    mat3 mtx;
    mtx[0] = normalize(vec3(camDir.z,0.,-camDir.x));
    mtx[1] = cross(camDir,mtx[0]);
    mtx[2] = camDir;
    vec3 ray = mtx*normalize(vec3(uv,1.));//direction of ray from camera

    vec3 p = camPos;
    vec3 l = vec3(0);                      //distance to surface
    for (int i = 0; i < 222; ++i)          //ray march
    {
        l = objec(p);
        float a = pow(length(l),1./pow(2.,loop))-1.;
        if(a!=a||a>99.){a=.2;}
        p += ray*a*.2;
    }
    float a = pow(length(l),1./pow(2.,loop))-1.;
    if(a!=a||a>99.){l=vec3(-1);}
	fragColor = vec4(normalize(l)*.5+.5,0);
}

void rotVec(inout vec4 v, float x, float y)
{
    y = asin(v.y) - y;
    if(-1.56 > y){y =-1.56;};
    if( 1.56 < y){y = 1.56;};
    float l = cos(y);
    x = -x;
    v.xz = vec2(v.x*cos(x) - v.z*sin(x),
                v.x*sin(x) + v.z*cos(x));
    v.xz*= l/length(v.xz);
    v.y  = sin(y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float keyW  = texture( iChannel1, vec2(87.5/256.,.25) ).x;
    float keyS  = texture( iChannel1, vec2(83.5/256.,.25) ).x;
    float keyA  = texture( iChannel1, vec2(65.5/256.,.25) ).x;
    float keyD  = texture( iChannel1, vec2(68.5/256.,.25) ).x;
    vec4 mouse  = texture( iChannel0, vec2(0.5,.5)/iResolution.xy);
    vec4 camDir = texture( iChannel0, vec2(1.5,.5)/iResolution.xy);
    vec4 camPos = texture( iChannel0, vec2(2.5,.5)/iResolution.xy);
    vec4 final = vec4(0.);

    //make sure camDir always is a normalized vector
    if(dot(camDir,camDir)==0.){camDir = normalize(vec4(-1,0,-1,0));}

    if(fragCoord.x == 0.5)//mod mouse texel
    {
        if(iMouse.z>0.){ mouse = iMouse; }
        else           { mouse.z = 0.;   }
		final = mouse;
    }
    if(fragCoord.x == 1.5)//mod camDir texel
    {
        if(iMouse.z>0. && mouse.z>0.)
        {
            mouse = (iMouse-mouse)*4./iResolution.y;
            rotVec(camDir, mouse.x, -mouse.y); 
        }
        final = camDir;
    }
    if(fragCoord.x == 2.5)//mod camPos texel
    {
        final = camPos + (keyW-keyS)*.01*camDir +
                         (keyD-keyA)*.01*normalize(vec4(camDir.z,0.,-camDir.x,0.));
    }
    fragColor = final;
}
