#define SPEED 1.

float sdCircle (vec2 _pos, float r){ // SDF Circle func
    return length(_pos) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x,fragCoord.y / iResolution.y);
    uv.x *= iResolution.x/iResolution.y;

    float circle = sdCircle(uv - vec2(.5,0.5), 0.5);
    float tex = texture( iChannel0,uv + iTime/10.*SPEED).x;  //moving tex
    float alpha = smoothstep(-0.5, circle, -0.43);
    vec4 col = vec4(tex * alpha);
    fragColor = col;
}
