vec2 WaveFunction(vec2 uv, float noiseScale, float scale, float octaveScale, int octaves, float offset)
{
    vec2 wavePoint = vec2(uv.x, 0);
    for(int octave = 1; octave <= octaves; octave++)
    {
        float coord = uv.x * 3.1415926 * float(octave) * octaveScale + 3.1415926/pow(2.0, float(octave)) + iTime;
        vec4 noise = texture(iChannel0, vec2(uv.x, coord)/10.0) / 256.0;
        wavePoint += vec2(0, (sin(coord * scale + noise.x*noiseScale + offset) + 1.0) / 2.0 / float(octave));
    }

    return wavePoint;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    float waveSpacing = 0.1f;
    float waveHeight = 10.0f;
    float definition = 10.0f;
    float dist = 0.0f;
    int waves = 10;
    for(int i = 0; i < waves; i++)
    {
        vec2 wavePoint = WaveFunction(uv, 50.0f, 3.0, 2.0, 5, float(i) * 10.0f) / vec2(1.0f, waveHeight) + vec2(0, waveSpacing) * float(i);
        float d = 1.0f - length(wavePoint - uv) * (definition + definition/2.0*sin(iTime * 2.0f + float(i)));  
        dist += clamp(d, 0.0f, 10.0f);
    }


    fragColor = vec4(0,dist/5.0f,dist/3.0f,1);
    fragColor +=vec4(pow(1.0f-(fragColor.r + fragColor.g + fragColor.b)/3.0f, 1.0f)) * vec4(0, 0, 1, 1) / 3.0f;
}
