float heart(vec2 p, vec2 center, float size) {
    float x = p.x - center.x;
    float y = p.y - center.y;
    float heartShape = (x * x + (y - sqrt(abs(x))) * (y - sqrt(abs(x)))) - size * size;
    return step(0.0, -heartShape); // <3
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 heartPos = vec2(0.15, 0.4); // Default Heart Posistion

    // Get mouse 
    vec2 mousePos = iMouse.xy / iResolution.xy;
    float heartSize = 0.1; 

    // Drag the heart
    if (iMouse.z > 0.0) {
        heartPos = mousePos;
    }

    // Colors
    vec3 baseRed = vec3(1.0, 0.2, 0.2);
    vec3 baseBlue = vec3(0.2, 0.2, 1.0);
    vec3 baseYellow = vec3(1.0, 1.0, 0.2);
    vec3 purpleColor = vec3(0.5, 0.0, 0.5);
    vec3 pinkColor = vec3(1.0, 0.75, 0.8);
    vec3 orangeColor = vec3(1.0, 0.5, 0.2);

    // Time-based color change-Heart
    float time = iTime * 0.6; // Adjust speed 
    float colorIndex = mod(time, 3.0); // Cycle through colors
    vec3 heartColor;

    if (colorIndex < 1.0) {
        heartColor = mix(pinkColor, orangeColor, colorIndex); // pink to orange
    } else if (colorIndex < 2.0) {
        heartColor = mix(orangeColor, purpleColor, colorIndex - 1.0); // orange to purple
    } else {
        heartColor = mix(purpleColor, pinkColor, colorIndex - 2.0); // purple to pink
    }

    // Color based on mouse 
    vec3 blueColor = mix(baseBlue, pinkColor, heartPos.x * 2.0);
    vec3 redColor = mix(baseRed, purpleColor, heartPos.x * 2.0);
    vec3 yellowColor = mix(baseYellow, orangeColor, heartPos.x * 2.0);

    // Create background with color split
    vec4 color;
    if (uv.x < 0.33) {
        color = vec4(blueColor, 1.0); // Blue
    } else if (uv.x < 0.67) {
        color = vec4(redColor, 1.0); // Red
    } else {
        color = vec4(yellowColor, 1.0); // Yellow
    }

    // Draw the heart
    float heartShape = heart(uv, heartPos, heartSize);

    // Start with background color
    fragColor = color; 


    if (heartShape > 0.0) {
        fragColor = mix(fragColor, vec4(heartColor, 1.0), heartShape); // Heart color
    }

    // blur 
    float blurAmount = smoothstep(0.0, 1.0, mousePos.x) * 0.005;

    vec4 blurredColor = vec4(0.0);
    float totalWeight = 0.0;

    for (float x = -4.0; x <= 4.0; x++) {
        for (float y = -4.0; y <= 4.0; y++) {
            vec2 offset = vec2(x, y) * blurAmount;
            vec2 sampleUv = uv + offset;


            sampleUv = clamp(sampleUv, 0.0, 1.0);

            // Recalculate background color
            vec4 sampleColor;
            if (sampleUv.x < 0.33) {
                sampleColor = vec4(blueColor, 1.0);
            } else if (sampleUv.x < 0.67) {
                sampleColor = vec4(redColor, 1.0);
            } else {
                sampleColor = vec4(yellowColor, 1.0);
            }

            blurredColor += sampleColor;
            totalWeight += 1.0;
        }
    }

    // Average the blurred colors
    fragColor = (blurredColor / totalWeight) * (1.0 - heartShape) + fragColor * heartShape; 
}
