bool canUse(vec3 color, vec3 step) {
  return color.r >= step.r && color.g >= step.g && color.b >= step.b;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec4 originalColor = texture(iChannel0, uv);
    //settings
    float intensity = 0.2;

    // Not the settings

    if (intensity != 0.0) {
        intensity = 0.35 + 0.7 * intensity;
    }

    float stepR = 0.8, stepG = 0.8, stepB = 0.8; 
    vec3 stepColor = vec3(stepR, stepG, stepB);
    vec4 blackWhiteColor = vec4(float(canUse(originalColor.rgb, stepColor)));
	blackWhiteColor.a = intensity;

    float dist = length(uv);

    vec4 blurredColor = vec4(0.0);
    blurredColor.a = 1.0;

    float offsetX = 0.01, offsetY = 0.01;

    for (float x = -1.0; x <= 1.0; x += 0.1) {
        for (float y = -1.0; y <= 1.0; y += 0.1) {
             blurredColor.rgb += texture(iChannel0, uv + vec2(x, y) * 0.006 + vec2(offsetX, offsetY)).rgb * intensity;
             blurredColor.rgb += texture(iChannel0, uv + vec2(x, y) * 0.004 - vec2(offsetX, offsetY)).rgb * intensity * 0.1;
        }
    }

    blurredColor.rgb /= 441.0;
    if (intensity != 0.) {
        fragColor = originalColor + mix(blackWhiteColor, blurredColor, intensity);
    } else {
        fragColor = originalColor;
    }
}
