vec3 base_R = vec3(1., 0, 0);
vec3 base_G = vec3(0, 1., 0);
vec3 base_B = vec3(0, 0, 1.);
//float pi = 3.14;

void mainImage( out vec4 p_Col, in vec2 p_pos ) 
{
    vec2 uv = p_pos/iResolution.xy - 0.5;
    uv.x = uv.x * iResolution.x/iResolution.y;

    uv.x = sin(iTime * 2.) * uv.x * 0.5 + sin(iTime) * 0.5;
    uv.y = sin(iTime * 2.) * uv.y * 0.5 + cos(iTime) * 0.5;
    //float col = smoothstep(-1., 1.,  pow(uv.x, 2.0) + pow(uv.y, 2.0));
    float col = pow(uv.x, 2.0) + pow(uv.y, 2.0) < 0.2? 1.: -1.;
    p_Col = vec4(col*vec3(sin(iTime), sin(iTime - 1.1), sin(iTime - 2.2)), 1.);
}
